import React, { useState, useEffect } from 'react';
import { getTasks, createTask, updateTask, deleteTask } from '../api';

function Dashboard({ onLogout }) {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [newPriority, setNewPriority] = useState('medium');
  const [newDueDate, setNewDueDate] = useState('');
  const [newReminderTime, setNewReminderTime] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editPriority, setEditPriority] = useState('medium');
  const [editDueDate, setEditDueDate] = useState('');
  const [editReminderTime, setEditReminderTime] = useState('');
  const [filter, setFilter] = useState('all');
  const [firedReminders, setFiredReminders] = useState(new Set());

  useEffect(() => {
    fetchTasks();
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      checkReminders(tasks);
    }, 5000);
    return () => clearInterval(interval);
  }, [tasks, firedReminders]);

  const checkReminders = (taskList) => {
    const now = new Date();
    taskList.forEach(task => {
      if (task.reminder_time && !task.completed && !firedReminders.has(task.id)) {
        const reminderTime = new Date(task.reminder_time);
        const timeDiff = Math.abs(reminderTime - now);
        
        if (timeDiff < 60000) {
          showNotification(task.title);
          setFiredReminders(prev => new Set([...prev, task.id]));
        }
      }
    });
  };

  const playAlarmSound = () => {
    try {
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      
      // Play alarm for 3 seconds with varying frequency
      const duration = 3;
      const now = audioContext.currentTime;
      
      // Create a pulsing alarm sound
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();
      const lfo = audioContext.createOscillator(); // Low frequency oscillator for pulsing
      
      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);
      
      lfo.frequency.value = 4; // Pulse 4 times per second
      lfo.connect(gainNode.gain);
      
      oscillator.frequency.value = 800;
      oscillator.type = 'square';
      
      gainNode.gain.setValueAtTime(0.3, now);
      gainNode.gain.linearRampToValueAtTime(0.3, now + duration);
      
      lfo.start(now);
      oscillator.start(now);
      
      lfo.stop(now + duration);
      oscillator.stop(now + duration);
    } catch (e) {
      console.log('Audio error:', e);
    }
  };

  const showNotification = (taskTitle) => {
    playAlarmSound();
    
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification('‚è∞ TASK REMINDER', {
        body: `Time for: ${taskTitle}`,
        icon: 'üìù',
        tag: 'task-reminder'
      });
    }
    alert(`‚è∞ REMINDER: ${taskTitle}`);
  };

  const fetchTasks = async () => {
    try {
      const response = await getTasks();
      setTasks(response.data);
      setError('');
    } catch (err) {
      setError('Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  const handleAddTask = async (e) => {
    e.preventDefault();
    if (!newTask.trim()) {
      setError('Task title cannot be empty');
      return;
    }

    try {
      await createTask(newTask, newPriority, newDueDate, newReminderTime);
      setNewTask('');
      setNewPriority('medium');
      setNewDueDate('');
      setNewReminderTime('');
      setError('');
      fetchTasks();
    } catch (err) {
      setError('Failed to create task');
    }
  };

  const handleToggleTask = async (task) => {
    try {
      await updateTask(task.id, task.title, !task.completed, task.priority, task.due_date, task.reminder_time);
      fetchTasks();
    } catch (err) {
      setError('Failed to update task');
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await deleteTask(taskId);
        fetchTasks();
      } catch (err) {
        setError('Failed to delete task');
      }
    }
  };

  const handleEditStart = (task) => {
    setEditingId(task.id);
    setEditTitle(task.title);
    setEditPriority(task.priority);
    setEditDueDate(task.due_date || '');
    setEditReminderTime(task.reminder_time || '');
  };

  const handleEditSave = async (taskId) => {
    if (!editTitle.trim()) {
      setError('Task title cannot be empty');
      return;
    }

    try {
      await updateTask(taskId, editTitle, false, editPriority, editDueDate, editReminderTime);
      setEditingId(null);
      fetchTasks();
    } catch (err) {
      setError('Failed to update task');
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return 'danger';
      case 'medium': return 'warning';
      case 'low': return 'info';
      default: return 'secondary';
    }
  };

  const formatDateTime = (dateTimeStr) => {
    if (!dateTimeStr) return '';
    const date = new Date(dateTimeStr);
    return date.toLocaleString();
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'completed') return task.completed;
    if (filter === 'pending') return !task.completed;
    return true;
  });

  return (
    <div className="container mt-5 mb-5">
      <div className="row justify-content-center">
        <div className="col-md-10">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h1>üìù My Tasks</h1>
            <button className="btn btn-danger" onClick={() => {
              if (window.confirm('Are you sure you want to logout?')) {
                localStorage.removeItem('token');
                onLogout();
              }
            }}>
              Logout
            </button>
          </div>

          {error && (
            <div className="alert alert-danger alert-dismissible fade show">
              {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}

          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">Add New Task</h5>
              <form onSubmit={handleAddTask}>
                <div className="row g-2">
                  <div className="col-md-4">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Task title..."
                      value={newTask}
                      onChange={(e) => setNewTask(e.target.value)}
                      maxLength="200"
                    />
                  </div>
                  <div className="col-md-2">
                    <select
                      className="form-select"
                      value={newPriority}
                      onChange={(e) => setNewPriority(e.target.value)}
                    >
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                    </select>
                  </div>
                  <div className="col-md-2">
                    <input
                      type="date"
                      className="form-control"
                      value={newDueDate}
                      onChange={(e) => setNewDueDate(e.target.value)}
                    />
                  </div>
                  <div className="col-md-2">
                    <input
                      type="datetime-local"
                      className="form-control"
                      title="Reminder time"
                      value={newReminderTime}
                      onChange={(e) => setNewReminderTime(e.target.value)}
                    />
                  </div>
                  <div className="col-md-2">
                    <button className="btn btn-primary w-100" type="submit">
                      Add
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>

          <div className="mb-3">
            <div className="btn-group" role="group">
              <button
                type="button"
                className={`btn ${filter === 'all' ? 'btn-primary' : 'btn-outline-primary'}`}
                onClick={() => setFilter('all')}
              >
                All ({tasks.length})
              </button>
              <button
                type="button"
                className={`btn ${filter === 'pending' ? 'btn-primary' : 'btn-outline-primary'}`}
                onClick={() => setFilter('pending')}
              >
                Pending ({tasks.filter(t => !t.completed).length})
              </button>
              <button
                type="button"
                className={`btn ${filter === 'completed' ? 'btn-primary' : 'btn-outline-primary'}`}
                onClick={() => setFilter('completed')}
              >
                Completed ({tasks.filter(t => t.completed).length})
              </button>
            </div>
          </div>

          {loading ? (
            <div className="text-center">
              <div className="spinner-border" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
            </div>
          ) : filteredTasks.length === 0 ? (
            <div className="alert alert-info">
              {filter === 'all' ? 'No tasks yet. Create one!' : `No ${filter} tasks.`}
            </div>
          ) : (
            <ul className="list-group">
              {filteredTasks.map((task) => (
                <li key={task.id} className="list-group-item">
                  {editingId === task.id ? (
                    <div className="row g-2">
                      <div className="col-md-3">
                        <input
                          type="text"
                          className="form-control"
                          value={editTitle}
                          onChange={(e) => setEditTitle(e.target.value)}
                          maxLength="200"
                        />
                      </div>
                      <div className="col-md-2">
                        <select
                          className="form-select"
                          value={editPriority}
                          onChange={(e) => setEditPriority(e.target.value)}
                        >
                          <option value="low">Low</option>
                          <option value="medium">Medium</option>
                          <option value="high">High</option>
                        </select>
                      </div>
                      <div className="col-md-2">
                        <input
                          type="date"
                          className="form-control"
                          value={editDueDate}
                          onChange={(e) => setEditDueDate(e.target.value)}
                        />
                      </div>
                      <div className="col-md-2">
                        <input
                          type="datetime-local"
                          className="form-control"
                          value={editReminderTime}
                          onChange={(e) => setEditReminderTime(e.target.value)}
                        />
                      </div>
                      <div className="col-md-3">
                        <button
                          className="btn btn-success btn-sm me-2"
                          onClick={() => handleEditSave(task.id)}
                        >
                          Save
                        </button>
                        <button
                          className="btn btn-secondary btn-sm"
                          onClick={() => setEditingId(null)}
                        >
                          Cancel
                        </button>
                      </div>
                    </div>
                  ) : (
                    <div className="d-flex justify-content-between align-items-center">
                      <div className="d-flex align-items-center flex-grow-1">
                        <input
                          type="checkbox"
                          className="form-check-input me-3"
                          checked={task.completed}
                          onChange={() => handleToggleTask(task)}
                        />
                        <div>
                          <span
                            style={{
                              textDecoration: task.completed ? 'line-through' : 'none',
                              color: task.completed ? '#999' : '#000',
                            }}
                          >
                            {task.title}
                          </span>
                          <div className="small text-muted mt-1">
                            {task.due_date && <span className="me-2">üìÖ {task.due_date}</span>}
                            {task.reminder_time && (
                              <span className="me-2" style={{ color: firedReminders.has(task.id) ? 'red' : 'inherit' }}>
                                ‚è∞ {formatDateTime(task.reminder_time)}
                              </span>
                            )}
                            <span className={`badge bg-${getPriorityColor(task.priority)}`}>
                              {task.priority}
                            </span>
                          </div>
                        </div>
                      </div>
                      <div>
                        <button
                          className="btn btn-sm btn-warning me-2"
                          onClick={() => handleEditStart(task)}
                        >
                          Edit
                        </button>
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => handleDeleteTask(task.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
